import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { MuiThemeProvider } from '@material-ui/core';
import { createMuiTheme, responsiveFontSizes } from '@material-ui/core/styles';

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

const useStyles = makeStyles((theme) => ({
    heroContent: {
    padding: theme.spacing(8, 0, 6),
      color: "#FFFFFF",
       top: "1rem",
     position: "relative",
    },
    img: {
      minHeight: "100vh",
      backgroundImage: `url(${process.env.PUBLIC_URL + "/images/laptop.jpg"})`,
      backgroundRepeat: "no-repeat",
      backgroundPosition: "center",
      backgroundSize: "cover",
      position: "relative",
      display: "flex",
      // justifyContent: "center",
      // alignItems: "center",

    },
    profileImg: {
      height: "25vh",
      width: "25vh",
      borderRadius: "50%",
      float: "none",
      shapeOutside: "circle()",
      top: "15rem",
      left: "17rem",
      position: "relative",
    },
    text: {
      top: "15rem",
      left: "20rem",
      position: "relative",

      // justifyContent: "center",
      // alignItems: "center",



      }


  }));

function Contact() {
    const classes = useStyles();
    return (



         <div className={classes.img}>


        <div className={classes.heroContent}>
          <Container maxWidth="sm">
          <Typography className={classes.title} variant="h2" align="center"  gutterBottom>
          Projects
        </Typography>
            <Typography variant="h5" align="center" color="textSecondary" paragraph>
              View My Projects!
            </Typography>
          </Container>
        </div>






         <div >
         <img className={classes.profileImg} src="/images/profile.jpg" alt=""/>
         </div>





        <div className={classes.text}>
        <Typography variant="h5"  gutterBottom>E-mail: Johnrrukaj@gmail.com</Typography>
         <br />
         <Typography variant="h5"  gutterBottom>Mobile: 07432481567</Typography>
         <br />
         <Typography variant="h5"  gutterBottom>LinkedIn: in/JohnRrukaj</Typography>
         <br />
         <Typography variant="h5"  gutterBottom>Github: Github.com/j-rrukaj</Typography>
        </div>


         </div>



    )
}

export default Contact
