import React from 'react';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { MuiThemeProvider } from '@material-ui/core';
import { createMuiTheme, responsiveFontSizes } from '@material-ui/core/styles';

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

const useStyles = makeStyles((theme) => ({
    heroContent: {
      padding: theme.spacing(12, 0, 6),
      color: "#FFFFFF"
    },
    backgroundImg: {
      minHeight: "100vh",
      backgroundImage: `url(${process.env.PUBLIC_URL + "/images/laptop.jpg"})`,
      backgroundRepeat: "no-repeat",
      backgroundPosition: "center",
      backgroundSize: "cover",
      position: "relative",
      display: "flex",  
    },
    profileImg: {
      height: "25vh",
      width: "25vh",
      borderRadius: "50%",
      float: "left",
      justifyContent: "center",
      alignContent: "center",
      alignItems: "center",
    },
    text: {
      position: "absolute"
      }, 
    title: {
      padding: "15px",
      padding: theme.spacing(8, 0, 6),
      },
  }));

function Contact() {
    const classes = useStyles();
    return (


      <div className={classes.backgroundImg} >


        <div className="text" >


        <Typography className={classes.title} variant="h2" align="center"  gutterBottom>
            Contact Me
          </Typography>



        <Typography className={classes.title} variant="h4" align="center"  gutterBottom>
            Details Below
          </Typography>


          <div>
          <img className={classes.profileImg} src="./images/profile.jpg" />
        </div>
        </div>


        <div className={classes.text}>

            <Typography variant="h5"  gutterBottom>E-mail: Johnrrukaj@gmail.com</Typography>
            <br />
            <Typography variant="h5"  gutterBottom>Mobile: 07432481567</Typography>
            <br />
            <Typography variant="h5"  gutterBottom>LinkedIn: in/JohnRrukaj</Typography>
            <br />
            <Typography variant="h5"  gutterBottom>Github: Github.com/j-rrukaj</Typography>
         </div>


          </div>














    )
}

export default Contact
